import { createResource, For, Show, Suspense } from "solid-js";
import { getPokemonList } from "./lib/pokemon";
import { PokemonCard } from "./components/pokemon-card";

export function PokemonPage() {
  const [pokemonList] = createResource(() => getPokemonList({ limit: 12 }));

  return (
    <div class="container mx-auto max-w-4xl p-4">
      <Suspense
        fallback={
          <div class="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <For each={Array(12).fill(null)}>
              {() => (
                <div class="h-40 bg-muted animate-pulse rounded-xl" />
              )}
            </For>
          </div>
        }
      >
        <Show when={pokemonList()}>
          {(data) => (
            <div class="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <For each={data().results}>
                {(pokemon) => <PokemonCard name={pokemon.name} />}
              </For>
            </div>
          )}
        </Show>
      </Suspense>
    </div>
  );
}

import { createResource, Show, Suspense } from "solid-js";
import { getPokemon } from "../lib/pokemon";
import { Card, CardContent } from "@/registry/new-york/ui/card";
import { PokemonImage } from "./pokemon-image";

interface PokemonCardProps {
  name: string;
}

export function PokemonCard(props: PokemonCardProps) {
  const [pokemon] = createResource(() => props.name, getPokemon);

  return (
    <Suspense
      fallback={
        <Card class="w-full">
          <CardContent class="flex flex-col items-center justify-center p-4">
            <div class="w-24 h-24 bg-muted animate-pulse rounded-full" />
            <div class="h-4 w-20 bg-muted animate-pulse rounded mt-2" />
          </CardContent>
        </Card>
      }
    >
      <Show when={pokemon()}>
        {(data) => (
          <Card class="w-full">
            <CardContent class="flex flex-col items-center justify-center p-4">
              <PokemonImage name={data().name} number={data().id} />
              <p class="text-sm font-medium capitalize mt-2">{data().name}</p>
            </CardContent>
          </Card>
        )}
      </Show>
    </Suspense>
  );
}

import { usePokemonImage } from "../hooks/use-pokemon";

interface PokemonImageProps {
  name: string;
  number: number;
}

export function PokemonImage(props: PokemonImageProps) {
  const imageUrl = usePokemonImage(props.number);

  return (
    <img
      src={imageUrl}
      alt={props.name}
      class="w-24 h-24 object-contain"
    />
  );
}

import { z } from "zod";

export async function getPokemonList({ limit = 10 }: { limit?: number } = {}) {
  try {
    const response = await fetch(
      `https://pokeapi.co/api/v2/pokemon?limit=${limit}`
    );
    return z
      .object({
        results: z.array(z.object({ name: z.string() })),
      })
      .parse(await response.json());
  } catch (error) {
    console.error(error);
    return null;
  }
}

export async function getPokemon(name: string) {
  try {
    const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);

    if (!response.ok) {
      throw new Error("Failed to fetch pokemon");
    }

    return z
      .object({
        name: z.string(),
        id: z.number(),
        sprites: z.object({
          front_default: z.string(),
        }),
        stats: z.array(
          z.object({
            base_stat: z.number(),
            stat: z.object({
              name: z.string(),
            }),
          })
        ),
      })
      .parse(await response.json());
  } catch (error) {
    console.error(error);
    return null;
  }
}

// This is a simple utility function that returns a Pokemon image URL.
// In a real application, this could be a more complex hook with state management.

export function usePokemonImage(number: number) {
  return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${number}.png`;
}
